import { useState } from "react";
import Axios from "axios";
import './Registration.css';

export default function Register() {

  const [data, setData] = useState({
    username: "",
    password: "",
    mail: "",
    photo: ""
  });
  const [response, setResponse] = useState("");
  const [login, setLogin] = useState("");

  async function Register(e) {
    e.preventDefault();
    try {

      const datax = new FormData();
      datax.append("username", data.username);
      datax.append("password", data.password);
      datax.append("mail", data.mail);
      datax.append("photo", data.photo);
      const r = await Axios.post("http://localhost:8000/register", datax);
      if (r.status === 400) {
        setResponse("Required credentials not filled");
      } else if (r.status === 409) {
        setResponse("User already exists");
      } else if (r.status === 201) {
        setResponse("User created");
      } else {
        setResponse("Internal Server Error")
      }
    } catch (error) {
      console.log(error);
    } finally {

    }
  };


  async function Login(e) {
    e.preventDefault();
    try {
      const datax = new FormData();
      datax.append("username", data.username);
      datax.append("password", data.password);
      const r = await Axios.post("http://localhost:8000/login", datax);
      if (r.status === 400) {
        setLogin("Required credentials not filled");
      } else if (r.status === 401) {
        setLogin("Unauthorized");
      } else if (r.status === 200) {
        setLogin("Authorized");
      } else {
        setLogin("Internal Server Error")
      }
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <div className="App">
      <form onSubmit={Register}>
        <input onChange={e => { setData({ ...data, username: e.target.value }) }} value={data.username} type="text" placeholder="Enter Username"></input>
        <input onChange={e => { setData({ ...data, password: e.target.value }) }} value={data.password} type="password" placeholder="Enter password"></input>
        <input onChange={e => { setData({ ...data, mail: e.target.value }) }} value={data.mail} type="email" placeholder="Enter mail"></input>
        <input onChange={e => { setData({ ...data, photo: e.target.files[0] }) }} type="file" placeholder="Enter photo"></input>
        <p>{response}</p>
        <button type="submit" >Create User</button>
      </form>
      <form onSubmit={Login}>
        <input onChange={e => { setData({ ...data, username: e.target.value }) }} value={data.username} type="text" placeholder="Enter Username"></input>
        <input onChange={e => { setData({ ...data, password: e.target.value }) }} value={data.password} type="password" placeholder="Enter password"></input>
        <p>{login}</p>
        <button type="submit">Submit</button>
      </form>
    </div>
  );
}
