import React, { useState } from "react";

export default function Hoodies() {
  const [pricing, setPricing] = useState(0);
  const [quantity, setQuantity] = useState(1);

  function addToCart(price) {
    setPricing((prevPrice) => prevPrice + price * quantity);
  }

  function PlaceOrder() {
    console.log(`The final price is $${pricing}`);
    alert(`Order placed. Your total is $${pricing}`);
    localStorage.setItem("totalbill", pricing);
    //window.location.href = "http://localhost:3000/confirmedorder";
  }

  function CancelOrder() {
    alert("Order cancelled");
    window.location.href = "http://localhost:3000/";
  }

  return (
    <div className="container">
      <section className="hoodies">
        <h1>Hoodies</h1>
        <button onClick={() => addToCart(50)} className="addhoodie">
          $50
        </button>
        <input
          type="number"
          className="quantityVal"
          value={quantity}
          onChange={(e) => setQuantity(parseInt(e.target.value, 10))}
        />
      </section>

      <section className="Tshirts">
        <h1>Tshirt</h1>
        <button onClick={() => addToCart(70)} className="addtshirt">
          $70
        </button>
        <input
          type="number"
          className="quantityVal"
          value={quantity}
          onChange={(e) => setQuantity(parseInt(e.target.value, 10))}
        />
      </section>

      <section className="cases">
        <h1>Case</h1>
        <button onClick={() => addToCart(20)} className="addcase">
          $20
        </button>
        <input
          type="number"
          className="quantityVal"
          value={quantity}
          onChange={(e) => setQuantity(parseInt(e.target.value, 10))}
        />
      </section>

      <p>Total Price: ${pricing}</p>
      <button onClick={PlaceOrder}>Place Order</button>
      <button onClick={CancelOrder}>Cancel Order</button>
    </div>
  );
}
