import "./App.css";
import Register from "./components/Registration";
import StoreFunctions from "./components/StoreFunctions";
import Login from "./components/Login";
import { useState } from "react";

export default function App() {
  const [user, setUser] = useState(null); // Provide an initial value

  if (!user) {
    // Use the setUser function to update the user state
    return <Register setUser={setUser}></Register>;
  } else {
    // You forgot to return the Login component
    return <Login user={user}></Login>;
  }

  return (
    <div className="container">
      <StoreFunctions></StoreFunctions>
    </div>
  );
}
