import {useState} from "react";
import Axios from "axios";



export default function StoreFunctions(){
        const [data,setData] = useState({
            title:"",
            description:"",
            quantity:"",
            extra:"",
            photo:""
        });
        const [response,setResponse] = useState("");

        async function addItems(e){
            e.preventDefault();
            try{
                const Datax = new FormData();
                Datax.append("title",data.title);
                Datax.append("description",data.description);
                Datax.append("quantity",data.quantity);
                Datax.append("extra",data.extra);
                for (let i =  0 ; i < data.photo.length ; i++){
                    Datax.append("data",data.photo)
                };
                Datax.append("photo",data.photo);
                const r = await Axios.post("http://localhost:8000/additem",Datax);
                if(r.status===201){
                    setResponse("Item Added")
                }else if (r.status===409){
                    setResponse("Required Fields not filled")
                }else if (r.status===400){
                    setResponse("Required Fields not filled")
                }else{
                    setResponse("Internal Server Error");
                }
            }catch(error){  
                console.log(error);
            }
        };
        

        return(
            <div className="container">
                <form onSubmit={addItems}>
                    <input onChange={e=>{setData({...data,title:e.target.value})}} type="text" placeholder="Enter title"></input>
                    <input onChange={e=>{setData({...data,description:e.target.value})}} type="text" placeholder="Enter description"></input>
                    <input onChange={e=>{setData({...data,quantity:e.target.value})}} type="text" placeholder="Enter quantity"></input>
                    <input onChange={e=>{setData({...data,extra:e.target.value})}} type="text" placeholder="Enter extra"></input>
                    <input onCanPlay={e=>{setData({...data,photo:e.target.files[0]})}}></input>
                    <button type="submit" style={{width:"40vw",height:"40vh"}}>Add Item</button>
                </form>
            </div>
        )
};
