import { useState } from "react";
import Axios from "axios";




export default function Home(){
    const [items,setItems] = useState([]);

    async function GetItems(e){
        e.preventDefault();
        try{
            const r = await Axios.get("http://localhost:8000/verify").then((r)=>{
                setItems(r.data);
            }).catch((e)=>{
                setItems(e.response.data);
            })
        }catch(error){
            console.log(error);
        }
    };

    return(
        <div className="container">
        <form onSubmit={GetItems}>
        <input onChange={e=>{setItems(e.target.value)}} value={items} type="text"></input>
        {items.length===0 ? <p>No results found</p> : (
            items.map((item)=>(
                <div>
                <p>{item.title}</p>
                </div>
            ))
        )}
        <button type="submit">Get Items</button>
        </form>

        
        </div>
    )
};
