import React, { useState } from "react";
import Axios from "axios";
import styles from "./register.module.css";

export default function Register() {
  const [loading, setLoading] = useState(false);
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [confirm, setConfirm] = useState("");
  const [mail, setMail] = useState("");
  const [status, setStatus] = useState("");
  const [photo, setPhoto] = useState("");
  const [response, setResponse] = useState("");

  async function Register(e) {
    e.preventDefault();
    try {
      setStatus("");
      setLoading(true);

      const response = await Axios.post("http://localhost:8000/register", {
        username: username,
        password: password,
        mail: mail,
        confirmpass: confirm,
      });

      if (response.status === 201) {
        setStatus("User Created");
        props.onAuth({ username, password, ...props.setUser });
        window.location.href = "/main.js";
      } else if (response.status === 409) {
        setStatus("Username or email already taken");
      } else if (response.status === 400) {
        setStatus("Error");
      } else {
        setStatus("Error!");
      }
    } catch (err) {
      console.error(err);
      setStatus("Error occurred during registration");
    } finally {
      setLoading(false);
      console.log("Execution Complete!");
    }
  }

  return (
    <div className="App">
      <form onSubmit={Register}>
        <h1>Register</h1>
        <input
          style={styles.usernameval}
          onChange={(e) => setData({ ...data, username: e.target.value })}
          value={data.username}
          type="text"
          required
          placeholder="Enter Username"
        />
        <input
          onChange={(e) => setData({ ...data, password: e.target.value })}
          value={data.password}
          type="password"
          required
          placeholder="Enter password"
        />
        <input
          onChange={(e) => setData({ ...data, mail: e.target.value })}
          value={data.mail}
          type="email"
          required
          placeholder="Enter mail"
        />
        <input
          onChange={(e) => setData({ ...data, bio: e.target.value })}
          value={data.bio}
          placeholder="Enter Bio"
        />
        <input
          onChange={(e) => setData({ ...data, photo: e.target.files[0] })}
          type="file"
        />
        <p>{response}</p>
        <button
          type="submit"
          style={{ width: "10vw", height: "10vh" }}
          disabled={loading}
        >
          {loading ? "Loading..." : "Register"}
        </button>
      </form>
    </div>
  );
}
