import React, { useState } from "react";

export default function Hoodies() {
  const [pricing, setPricing] = useState(0);

  function addToCart(price) {
    setPricing((prevPrice) => prevPrice + price);
  }

  function PlaceOrder() {
    let finalPrice = 0;
    finalPrice += pricing;
    console.log(`The final price is ${finalPrice}`);
    alert(`Order placed your total is $${finalPrice}`);
  }

  return (
    <div className="container">
      <h1>Hoodies</h1>
      <button onClick={() => addToCart(50)} className="addhoodie">
        $50
      </button>
      <h1>Tshirt</h1>
      <button onClick={() => addToCart(70)} className="addtshirt">
        $70
      </button>
      <h1>Case</h1>
      <button onClick={() => addToCart(20)} className="addcase">
        $20
      </button>
      <p>Total Price: ${pricing}</p>
      <button onClick={PlaceOrder}>Place Order</button>
    </div>
  );
}
