import React, { useRef } from "react";
import Axios from "axios";
import { useState } from "react";
//import { ChakraProvider } from "@chakra-ui/react";
import "./register.css";

export default function Register(props) {
  const [loading, setLoading] = useState(false);
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [confirm, setConfirm] = useState("");
  const [mail, setMail] = useState("");
  const [status, setStatus] = useState("");
  const [photo, setPhoto] = useState("");

  async function Register(e) {
    e.preventDefault();
    try {
      setStatus("");
      setLoading(true);

      const response = await Axios.post(
        "http://localhost:8000/register",
        {
          username: username,
          password: password,
          mail: mail,
          confirmpass: confirm,
          photo: photo,
        },
        {
          headers: {
            "Content-Type": "multipart/form-data",
          },
        }
      );

      if (response.status === 201) {
        setStatus("User Created");
        props.onAuth({ username, password, ...props.setUser });
        window.location.href = "/main.js";
      } else if (response.status === 409) {
        setStatus("Username or email already taken");
      } else if (response.status === 400) {
        setStatus("Error");
      } else {
        setStatus("Error!");
      }
    } catch (err) {
      console.error(err);
      setStatus("Error occurred during registration");
    } finally {
      setLoading(false);
      console.log("Execution Complete!");
    }
  }

  return (
    //<ChakraProvider>
    <div className="registry-container">
      <form onSubmit={Register}>
        <h1>Register</h1>
        <input
          onChange={(e) => setUsername(e.target.value)}
          placeholder="Enter Username"
          value={username}
          type="text"
          required
        />
        <input
          onChange={(e) => setPassword(e.target.value)}
          placeholder="Enter Password"
          value={password}
          type="password"
          required
        />
        <input
          onChange={(e) => setConfirm(e.target.value)}
          placeholder="Enter Confirm Password"
          value={confirm}
          type="password"
          required
        />
        <input
          onChange={(e) => setMail(e.target.value)}
          placeholder="Enter Mail"
          value={mail}
          type="email"
          required
        />
        <input
          type="file"
          onChange={(e) => {
            setPhoto(e.target.files[0]);
          }}
        ></input>
        <p>{status}</p>
        <button type="submit">{loading ? "Loading..." : "Create User"}</button>
      </form>
    </div>
    //</ChakraProvider>
  );
}
