import React, { useState,useReducer, useRef} from "react";
import Axios from "axios";

export default function Login(props) {
  const[data,setData] = useState({
    username:"",
    password:""
  });
  const [response, setResponse] = useState("");
  const [loading,setLoading] = useReducer((loading)=>!loading,false);
  const username = useRef();
  const password = useRef();



  async function handleLogin(e) {
    e.preventDefault();

    try {
      setLoading(true);
      const formData = new FormData();
      formData.append("username", data.username);
      formData.append("password", data.password);

      const response = await Axios.post(
        "http://localhost:8000/register/login",
        formData,
        {
          headers: {
            "Content-Type": "multipart/form-data",
          },
        }
      ).then((response)=>{
        if(response.status===200){
          alert(`Welcome back ${props.user}`);
          setResponse("Authorized")
        }else if(response.status===401){
          alert(`${data.username} Unauthorized`);
          setResponse("Unauthorized")
        }else if (response.status===400){
          setResponse("Username and password missing");
        }else{
          setResponse("Server Error");
        }
      });
      setLoading(false);
    } catch (error) {
      console.error(error);
   
    }
    finally{
      username.current.value ="";
      password.current.value = "";
    }
  }

  return (
    <div className="container">
    <div className="login">
      <h1>Log In</h1>
      <form action="loginForm">
        <div className="fields">
          <p>Username : </p>
          <input
            type="text"
            placeholder="Enter Username"
            id="fieldsx"
            className="usernameval"
          />
          <p>Password : </p>
          <input
            type="text"
            placeholder="Enter Password"
            id="fieldsx"
            className="passwordval"
            required=""
          />
          <br />
          <input
            type="submit"
            defaultValue="Login"
            className="loginsub"
            onclick="runalert()"
          />
          <br />
          <div className="register">
            <h2>
              Not Registered Yet? Click Here to{" "}
              <a
                href=""
                onclick="systemworking()"
                className="hone"
                id="registering"
              >
                Register
              </a>
            </h2>
            <div className="alternate" onclick="runner()">
              <p>Or Sign in with </p>
              <input type="image" src="yt.png" alt="" />
              <input type="image" src="twitter.png" alt="" />
              <input type="image" src="tiktok.png" alt="" />
            </div>
          </div>
          <a href="main.html" className="hone">
            Back Home?
          </a>
        </div>
      </form>
    </div>
  </div>
  
  );
}
