import React, { useState } from "react";
import Axios from "axios";
import "./css/Register.css";

export default function Register() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [mail, setMail] = useState("");
  const [bio, setBio] = useState("");
  const [photo, setPhoto] = useState(null);
  const [response, setResponse] = useState("");
  const [loading, setLoading] = useState(false);

  async function handleRegister(e) {
    e.preventDefault();

    if (response.length !== 0) {
      setResponse("");
    }

    // Form validation
    if (!username || !password || !confirmPassword || !mail) {
      setResponse("Required credentials not filled");
      return;
    }

    if (password !== confirmPassword) {
      setResponse("Passwords do not match");
      return;
    }

    try {
      setLoading(true);

      const datax = new FormData();
      datax.append("username", username);
      datax.append("password", password);
      datax.append("mail", mail);
      datax.append("bio", bio);
      if (photo) {
        datax.append("photo", photo);
      }

      const r = await Axios.post("http://localhost:8000/register", datax, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      });

      if (r.status === 201) {
        setResponse("User created");
      } else if (r.status === 409) {
        setResponse("User already exists");
      } else if (r.status === 400) {
        setResponse("Required credentials not filled");
      } else {
        setResponse("Internal Server Error");
      }

      setLoading(false);
    } catch (error) {
      console.error(error);
      setResponse("Error occurred. Please try again.");
      setLoading(false);
    }
  }

  return (
    <div className="App">
      <form onSubmit={handleRegister}>
        <h1>Register</h1>
        <input
          onChange={(e) => setUsername(e.target.value)}
          value={username}
          type="text"
          required
          placeholder="Enter Username"
        />
        <input
          onChange={(e) => setPassword(e.target.value)}
          value={password}
          type="password"
          required
          placeholder="Enter password"
        />
        <input
          onChange={(e) => setConfirmPassword(e.target.value)}
          value={confirmPassword}
          type="password"
          required
          placeholder="Confirm Password"
        />
        <input
          onChange={(e) => setMail(e.target.value)}
          value={mail}
          type="email"
          required
          placeholder="Enter mail"
        />
        <input
          onChange={(e) => setBio(e.target.value)}
          value={bio}
          placeholder="Enter Bio"
        />
        <input
          onChange={(e) => setPhoto(e.target.files[0])}
          type="file"
        />
        <p>{response}</p>
        <button
          type="submit"
          style={{ width: "10vw", height: "10vh" }}
          disabled={loading}
        >
          {loading ? "Loading..." : "Register"}
        </button>
      </form>
    </div>
  );
}
