import React,{useState} from "react";
import Axios from "axios";
import "./Login.css";

export default function Login(){
    const [data, setData] = useState({
        username: "",
        password: "",
        mail: "",
        photo: ""
      });
      const [login, setLogin] = useState("");




    async function Login(e) {
        e.preventDefault();
        if(setLogin.length!==0){
            setLogin("");
        }
        try {
          const datax = new FormData();
          datax.append("username", data.username);
          datax.append("password", data.password);
          const r = await Axios.post("http://localhost:8000/login", datax);
          if (r.status === 200) {
            setLogin("Logged in");
          } else if (r.status === 401) {
            setLogin("Unauthorized");
          } else if (r.status===400) {
            setLogin("Required credentials not filled");
          } else {
            setLogin("Internal Server Error")
          }
        } catch (error) {
          console.log(error);
        }
      };

      return(
        <form onSubmit={Login}>
        <input onChange={e => { setData({ ...data, username: e.target.value }) }} value={data.username} type="text" placeholder="Enter Username"></input>
        <input onChange={e => { setData({ ...data, password: e.target.value }) }} value={data.password} type="password" placeholder="Enter password"></input>
        <p>{login}</p>
        <button type="submit">Submit</button>
      </form>
      )
};
